package javastart.a02_operator;

public class A03_space {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 *  \t :탭간격처리 
		 *  println(): 줄 변경 해서 출력
		 *  print(): 줄 변경 없이 출력
		 *  \n: 줄바꿈처리
		 * ctrl+F11 : 실행 
		 */
		System.out.println("no\t국어\t영어\t수학\t총점\t평균");
		System.out.print("안녕!!\n");
		System.out.println("홍길동!!\n");
	}

}
