package javastart.e01_string;

public class A01_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
/*
String은 객체이면 문자열을 할당할 수 있는 기능을 가지고 있다.
 * */
		// 문자열 객체를 할당하는 형식..
		String str = new String("Hello");
		String str2 = new String("Hello");
		
		// 문자열을 할당하는 형식(간략화)
		String str3="Hello";
		String str4="Hello";
		
		
		System.out.println("비교 new String:"+(str == str2));
		System.out.println("비교 문자열할당"+(str3 == str4));
		// 반드시 문자열을 비교할 때는 equals를 활용하여야 한다.
		System.out.println("비교(equals) new String:"+(str.equals(str2)) );
		System.out.println("비교(equals) 문자열할당:"+(str3.equals(str4)));
		
		
		
		
	}

}
